using UnityEngine;using System.Collections;public class LevelManager{    public static int[] HeyMister = { 67, 69, 71, 69, 62, 69, 67, 69, 62, 64, 62, 59 };    public static int[] Twinkle = { 57, 64, 66, 64, 62, 61, 59, 57 };    public static int[] GenerateLevel(int n)    {        int[] notes = new int[n];        notes[0] = Random.Range(Constants.MinNote + 1, Constants.MaxNote + 1);        for (int i = 1; i < n; i++)        {            notes[i] = Random.Range(Constants.MinNote, Constants.MaxNote + 1);            while (notes[i] == notes[i - 1])            {                notes[i] = Random.Range(Constants.MinNote, Constants.MaxNote + 1);            }        }        return notes;    }}